var IndexStore = assign({}, EventEmitter.prototype, {
    emitChange: function() {
        this.emit(CHANGE_EVENT);
    },
    addChangeListener: function(callback) {
        this.on(CHANGE_EVENT, callback);
    },

    removeChangeListener: function(callback) {
        this.removeListener(CHANGE_EVENT, callback);
    },

    getAll: function () {
        return _movies;
    },
    getMovie: function (id) {
        return _movies.filter(function (movie) {
            return movie.movie_id == id;
        })[0];
    },

    login: function (username, password) {
        var user = _users.filter(function (u) {
            return u.username == username && u.password == password;
        })[0] || {id: 0};
        _user_id = user.id;
    },
    getUserId: function () {
        return _user_id;
    }
});

AppDispatcher.register(function (payload) {
    switch (payload.actionType) {
        case "GET_MOVIE":
            IndexStore.getMovie(payload.id);
            break;
        case "LOGIN":
            IndexStore.login(payload.username, payload.password);
            break;
    }

    IndexStore.emitChange();
    return true;
});

